# -*- coding: utf-8 -*-# @Author  :import requests,jsonfrom   Public.log import run_log as loggerfrom config import  zhilogger.info('接口请求类型封装')class jiekou():    def __init__(self):        self.headers = zhi.headers    def get(self, url,params):        try:            r = requests.get(url, params=params,headers=self.headers)            r.encoding = 'UTF-8'            res = json.loads(r.text)            return {'code':0,'result':res}        except Exception as e:            logger.info('get请求出错原因:%s'%e)            return {'code': 1, 'result': 'get请求出错原因:%s'%e}    def post(self, url, params):        data = json.dumps(params)        try:            r =requests.post(url,params=data,headers=self.headers)            res = json.loads(r.text)            return {'code': 0, 'result': res}        except Exception as e:            logger.info('post请求出错原因:%s' % e)            return {'code': 1, 'result': 'post请求出错原因:%s' % e}    # def delfile(self,url,params):    #     try:    #         del_word=requests.delete(url,params=params,headers=self.headers)    #         json_response=json.loads(del_word.text)    #         return {'code': 0, 'result': json_response}    #     except Exception as e:    #         LOG.info('del请求出错，出错原因:%s' % e)    #         return {'code': 1, 'result': 'del请求出错，出错原因:%s' % e}    # def putfile(self,url,params):    #     try:    #         data=json.dumps(params)    #         me=requests.put(url,data)    #         json_response=json.loads(me.text)    #         return {'code': 0, 'result': json_response}    #     except Exception as e:    #         LOG.info('put请求出错，出错原因:%s' % e)    #         return {'code': 1, 'result': 'put请求出错，出错原因:%s' % e}